// client/src/pages/Profile.jsx
import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from '../context/AuthContext';
import { fetchStories, fetchThreads } from '../api';
import { Link } from 'react-router-dom';

const Profile = () => {
  const { user } = useContext(AuthContext);
  const [stories, setStories] = useState([]);
  const [threads, setThreads] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user) return;

    setLoading(true);
    Promise.all([fetchStories(), fetchThreads()])
      .then(([storiesRes, threadsRes]) => {
        // replace `.author` string here with whatever you actually typed
        const authoredStories = storiesRes.data.filter(
          s => s.author === user.email  
        );
        const authoredThreads = threadsRes.data.filter(
          t => t.author === user.email  
        );
        setStories(authoredStories);
        setThreads(authoredThreads);
      })
      .finally(() => setLoading(false));
  }, [user]);

  if (!user) return <p className="p-6">Please log in.</p>;
  if (loading) return <p className="p-6">Loading…</p>;

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h2 className="text-2xl font-bold mb-2">
        Welcome, {user.displayName || user.email}
      </h2>
      <section className="mb-8">
        <h3 className="text-xl font-semibold mb-2">Your Stories</h3>
        {stories.length === 0 ? (
          <p className="text-gray-500">You haven’t submitted any stories yet.</p>
        ) : (
          <ul className="list-disc list-inside">
            {stories.map(s => (
              <li key={s._id}>
                <Link to={`/stories/${s._id}`} className="text-blue-600 hover:underline">
                  {s.title}
                </Link>
              </li>
            ))}
          </ul>
        )}
      </section>
      <section>
        <h3 className="text-xl font-semibold mb-2">Your Threads</h3>
        {threads.length === 0 ? (
          <p className="text-gray-500">You haven’t started any threads yet.</p>
        ) : (
          <ul className="list-disc list-inside">
            {threads.map(t => (
              <li key={t._id}>
                <Link to={`/forum/${t._id}`} className="text-blue-600 hover:underline">
                  {t.title}
                </Link>
              </li>
            ))}
          </ul>
        )}
      </section>
    </div>
  );
};

export default Profile;
