import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:5000/api',  // adjust if you deployed elsewhere
  headers: { 'Content-Type': 'application/json' }
});

// Thread endpoints
export const fetchThreads   = () => api.get('/threads');
export const fetchThreadById = id => api.get(`/threads/${id}`);
export const createThread   = data => api.post('/threads', data);

// Story endpoints (you can add these when your stories API is ready)
export const fetchStories  = () => api.get('/stories');
export const createStory   = formData => api.post('/stories', formData, {
  headers: { 'Content-Type': 'multipart/form-data' }
});
