import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from '../context/AuthContext';
import { fetchUserStories, fetchUserThreads } from '../api';
import { Link } from 'react-router-dom';

const Profile = () => {
  const { user } = useContext(AuthContext);
  const [stories, setStories] = useState([]);
  const [threads, setThreads] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError]     = useState('');

  useEffect(() => {
    if (!user?.email) return;

    setLoading(true);
    Promise.all([
      fetchUserStories(user.email),
      fetchUserThreads(user.email)
    ])
      .then(([storiesRes, threadsRes]) => {
        setStories(storiesRes.data);
        setThreads(threadsRes.data);
      })
      .catch(err => {
        console.error(err);
        setError('Failed to load your contributions');
      })
      .finally(() => setLoading(false));
  }, [user]);

  if (!user) {
    return <p className="p-6">Please log in to view your profile.</p>;
  }

  if (loading) {
    return <p className="p-6">Loading your stories and threads…</p>;
  }

  if (error) {
    return <p className="p-6 text-red-500">{error}</p>;
  }

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h2 className="text-2xl font-bold mb-2">
        Welcome, {user.displayName || user.email}
      </h2>
      <p className="text-gray-600 mb-6">Your contributions to TravelConnect:</p>

      <section className="mb-8">
        <h3 className="text-xl font-semibold mb-2">Your Stories</h3>
        {stories.length === 0 ? (
          <p className="text-gray-500">You haven’t submitted any stories yet.</p>
        ) : (
          <ul className="list-disc list-inside space-y-1">
            {stories.map(s => (
              <li key={s._id}>
                <Link to={`/stories/${s._id}`} className="text-blue-600 hover:underline">
                  {s.title}
                </Link>
              </li>
            ))}
          </ul>
        )}
      </section>

      <section>
        <h3 className="text-xl font-semibold mb-2">Your Threads</h3>
        {threads.length === 0 ? (
          <p className="text-gray-500">You haven’t started any threads yet.</p>
        ) : (
          <ul className="list-disc list-inside space-y-1">
            {threads.map(t => (
              <li key={t._id}>
                <Link to={`/forum/${t._id}`} className="text-blue-600 hover:underline">
                  {t.title}
                </Link>
              </li>
            ))}
          </ul>
        )}
      </section>
    </div>
  );
};

export default Profile;
