import React, { useState } from 'react';
import { createStory } from '../api';
import { useNavigate } from 'react-router-dom';

const SubmitStory = () => {
  const [title, setTitle]       = useState('');
  const [content, setContent]   = useState('');
  const [image, setImage]       = useState(null);
  const [tags, setTags]         = useState('');
  const [author, setAuthor]     = useState('');
  const navigate = useNavigate();

  const handleSubmit = async e => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('title', title);
    formData.append('content', content);
    formData.append('author', author);
    formData.append('tags', tags);
    if (image) formData.append('image', image);

    try {
      await createStory(formData);
      navigate('/');    // Go back to Home or Stories page
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="p-6 max-w-xl mx-auto">
      <h2 className="text-2xl font-semibold mb-4">Share Your Travel Story</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Title, Content & Author */}
        <input type="text" placeholder="Title" value={title}
          onChange={e => setTitle(e.target.value)}
          className="w-full border p-2 rounded" required />

        <textarea placeholder="Your experience..." value={content}
          onChange={e => setContent(e.target.value)}
          className="w-full border p-2 rounded h-32" required />

        <input type="text" placeholder="Your name" value={author}
          onChange={e => setAuthor(e.target.value)}
          className="w-full border p-2 rounded" required />

        {/* Tags */}
        <input type="text" placeholder="Tags (comma-separated)" value={tags}
          onChange={e => setTags(e.target.value)}
          className="w-full border p-2 rounded" />

        {/* Image Upload */}
        <input type="file" accept="image/*"
          onChange={e => setImage(e.target.files[0])}
          className="w-full" />

        <button type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
          Submit Story
        </button>
      </form>
    </div>
  );
};

export default SubmitStory;
