import React, { useState } from 'react';
import ForumThread from '../components/ForumThread';

<ForumThread
  title="Best sleeper buses in Maharashtra"
  author="Sujay"
  replies={12}
  createdAt="2025-09-15"
/>

const Forum = () => {
  const [threads, setThreads] = useState([
    { id: 1, title: "Best sleeper buses in Maharashtra", replies: 12 },
    { id: 2, title: "Tips for solo travel by bus", replies: 8 },
  ]);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-semibold mb-4">Community Forum</h2>
      <ul className="space-y-4">
        {threads.map((thread) => (
          <li key={thread.id} className="border p-4 rounded shadow-sm">
            <h3 className="text-lg font-bold">{thread.title}</h3>
            <p>{thread.replies} replies</p>
            {/* TODO: Link to thread detail page */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Forum;
