import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from '../context/AuthContext';
import { fetchUserStories, fetchUserThreads } from '../api';

const Profile = () => {
  const { user } = useContext(AuthContext);
  const [stories, setStories] = useState([]);
  const [threads, setThreads] = useState([]);

  useEffect(() => {
    if (user) {
      fetchUserStories(user.email).then(res => setStories(res.data));
      fetchUserThreads(user.email).then(res => setThreads(res.data));
    }
  }, [user]);

  if (!user) return <p className="p-6">Please log in to view your profile.</p>;

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Welcome, {user.displayName || user.email}</h2>
      <p className="text-gray-600 mb-6">Your contributions to TravelConnect:</p>

      <h3 className="text-xl font-semibold mb-2">Your Stories</h3>
      <ul className="space-y-2 mb-6">
        {stories.map(s => (
          <li key={s._id} className="border p-3 rounded">{s.title}</li>
        ))}
      </ul>

      <h3 className="text-xl font-semibold mb-2">Your Threads</h3>
      <ul className="space-y-2">
        {threads.map(t => (
          <li key={t._id} className="border p-3 rounded">{t.title}</li>
        ))}
      </ul>
    </div>
  );
};

export default Profile;
