import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:/api', 
  headers: { 'Content-Type': 'application/json' }
});

// Thread endpoints
export const fetchThreads     = () => api.get('/threads');
export const fetchThreadById  = (id) => api.get(`/threads/${id}`);
export const createThread     = (data) => api.post('/threads', data);
export const postReply        = (threadId, data) => api.post(`/threads/${threadId}/replies`, data);
export const fetchUserThreads = (email) => api.get(`/threads/user/${email}`);

// Story endpoints
export const fetchStories     = () => api.get('/stories');
export const createStory      = (formData) => api.post(
  '/stories',
  formData,
  { headers: { 'Content-Type': 'multipart/form-data' } }
);
export const fetchUserStories = (email) => api.get(`/stories/user/${email}`);
