// client/src/pages/SubmitStory.jsx

import React, { useState, useContext } from 'react';
import { createStoryWithUrl } from '../api';
import { AuthContext } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const SubmitStory = () => {
  const { user } = useContext(AuthContext);
  const [title, setTitle]     = useState('');
  const [content, setContent] = useState('');
  const [tags, setTags]       = useState('');
  const [imageUrl, setImageUrl] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault(); // prevents page reload
  
    const payload = {
      title,
      content,
      tags: tags.split(',').map(t => t.trim()),
      author: user.email,
      imageUrl
    };
  
    try {
      await createStoryWithUrl(payload); // API call
      navigate('/profile');              // redirect after success
    } catch (err) {
      console.error('Submission failed:', err);
    }
  };

  return (
    <div className="p-6 max-w-xl mx-auto">
      <h2 className="text-2xl font-semibold mb-4">Share Your Travel Story</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={e => setTitle(e.target.value)}
          className="w-full border p-2 rounded"
          required
        />

        <textarea
          placeholder="Your experience..."
          value={content}
          onChange={e => setContent(e.target.value)}
          className="w-full border p-2 rounded h-32"
          required
        />

        <input
          type="text"
          value={user.email}
          disabled
          className="w-full border p-2 rounded bg-gray-100"
        />

        <input
          type="text"
          placeholder="Image URL"
          value={imageUrl}
          onChange={e => setImageUrl(e.target.value)}
          className="w-full border p-2 rounded"
        />

        <input
          type="text"
          placeholder="Tags (comma-separated)"
          value={tags}
          onChange={e => setTags(e.target.value)}
          className="w-full border p-2 rounded"
        />

        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Submit Story
        </button>
      </form>
    </div>
  );
};

export default SubmitStory;
